<?php// Health Check para Vercelheader('Content-Type: application/json; charset=utf-8');header('Access-Control-Allow-Origin: *');header('Access-Control-Allow-Methods: GET, POST, PUT, DELETE, OPTIONS');header('Access-Control-Allow-Headers: Content-Type, Authorization');// Tratar requisições OPTIONS (preflight)if ($_SERVER['REQUEST_METHOD'] === 'OPTIONS') {    http_response_code(200);    exit();}try {    // Incluir configuração    require_once 'config-vercel.php';        $health = checkSystemHealth();        echo json_encode([        'success' => true,        'message' => 'Sistema funcionando',        'health' => $health,        'environment' => [            'php_version' => PHP_VERSION,            'server' => $_SERVER['SERVER_SOFTWARE'] ?? 'Unknown',            'vercel' => isset($_ENV['VERCEL']) ? 'true' : 'false',            'timestamp' => date('Y-m-d H:i:s')        ]    ]);    } catch (Exception $e) {    http_response_code(500);    echo json_encode([        'success' => false,        'message' => 'Erro no sistema',        'error' => $e->getMessage(),        'timestamp' => date('Y-m-d H:i:s')    ]);}?>