<?php// API Router - Sistema Help Desk TITANIUMrequire_once 'config.php';// Função para roteamentofunction route($method, $path) {    $path = trim($path, '/');    $segments = explode('/', $path);        $controller = $segments[0] ?? '';    $action = $segments[1] ?? '';    $id = $segments[2] ?? null;        // Configurar parâmetros GET para os controladores    $_GET['path'] = $path;    if ($id) {        $_GET['id'] = $id;    }        switch ($controller) {        case 'auth':            require_once 'auth.php';            break;                    case 'tickets':            require_once 'tickets.php';            break;                    case 'users':            require_once 'users.php';            break;                    case 'categories':            require_once 'categories.php';            break;                    case 'priorities':            require_once 'priorities.php';            break;                    case 'statuses':            require_once 'statuses.php';            break;                    case 'dashboard':            require_once 'dashboard.php';            break;                    default:            ApiResponse::error('Endpoint não encontrado', 404);    }}// Obter método e path$method = $_SERVER['REQUEST_METHOD'];$requestUri = $_SERVER['REQUEST_URI'];$scriptName = $_SERVER['SCRIPT_NAME'];// Remover o script name da URI$path = str_replace(dirname($scriptName), '', $requestUri);$path = parse_url($path, PHP_URL_PATH);// Roteamentoroute($method, $path);?>