<?php// API simples para visualizar um ticket específicoheader('Content-Type: application/json; charset=utf-8');try {    require_once 'config.php';        $db = new Database();    $conn = $db->getConnection();        // Obter ID do ticket da URL    $ticketId = $_GET['id'] ?? null;        if (!$ticketId) {        echo json_encode([            'success' => false,            'message' => 'ID do ticket não fornecido',            'data' => null        ]);        exit;    }        // Query para buscar o ticket específico    $query = "        SELECT             t.id, t.title, t.description, t.priority, t.status, t.category, t.created_at, t.updated_at,            u.name as user_name, u.email as user_email        FROM tickets t        LEFT JOIN users u ON t.user_id = u.id        WHERE t.id = ?    ";        $stmt = $conn->prepare($query);    $stmt->execute([$ticketId]);    $ticket = $stmt->fetch();        if (!$ticket) {        echo json_encode([            'success' => false,            'message' => 'Ticket não encontrado',            'data' => null        ]);        exit;    }        echo json_encode([        'success' => true,        'message' => 'Ticket carregado com sucesso',        'data' => $ticket    ]);    } catch (Exception $e) {    echo json_encode([        'success' => false,        'message' => 'Erro: ' . $e->getMessage(),        'data' => null    ]);}?>