<?php// API muito simples para debugheader('Content-Type: application/json; charset=utf-8');try {    require_once 'config.php';        $db = new Database();    $conn = $db->getConnection();        // Query simples    $stmt = $conn->query("SELECT id, title, description, priority, created_at FROM tickets ORDER BY created_at DESC LIMIT 10");    $tickets = $stmt->fetchAll();        echo json_encode([        'success' => true,        'message' => 'Tickets carregados com sucesso',        'data' => $tickets    ]);    } catch (Exception $e) {    echo json_encode([        'success' => false,        'message' => 'Erro: ' . $e->getMessage(),        'data' => null    ]);}?>