-- Tabela de perfis de técnicosCREATE TABLE IF NOT EXISTS technician_profiles (    id INT PRIMARY KEY AUTO_INCREMENT,    user_id INT NOT NULL,    -- Dados pessoais    naturalidade VARCHAR(120) NULL,    rg VARCHAR(50) NULL,    cpf VARCHAR(20) NULL,    data_nascimento DATE NULL,    genero VARCHAR(30) NULL,    nome_mae VARCHAR(255) NULL,    nome_pai VARCHAR(255) NULL,        -- Endereço pessoal    cep VARCHAR(12) NULL,    endereco VARCHAR(255) NULL,    numero VARCHAR(20) NULL,    bairro VARCHAR(120) NULL,    cidade VARCHAR(120) NULL,    estado VARCHAR(3) NULL,    pais VARCHAR(80) NULL,        -- Contatos    celular1 VARCHAR(20) NULL,    celular2 VARCHAR(20) NULL,    whats VARCHAR(20) NULL,    telefone_fixo VARCHAR(20) NULL,        -- Referência bancária    chave_pix VARCHAR(120) NULL,    banco VARCHAR(120) NULL,    cod_banco VARCHAR(10) NULL,    agencia VARCHAR(20) NULL,    conta VARCHAR(30) NULL,    tipo_conta ENUM('corrente','poupanca') NULL,    operacao VARCHAR(10) NULL,    favorecido VARCHAR(255) NULL,        -- Empresa (opcional)    emp_razao_social VARCHAR(255) NULL,    emp_nome_fantasia VARCHAR(255) NULL,    emp_cnpj VARCHAR(20) NULL,    emp_inscricao_estadual VARCHAR(50) NULL,    emp_inscricao_municipal VARCHAR(50) NULL,    emp_cep VARCHAR(12) NULL,    emp_endereco VARCHAR(255) NULL,    emp_numero VARCHAR(20) NULL,    emp_bairro VARCHAR(120) NULL,    emp_cidade VARCHAR(120) NULL,    emp_estado VARCHAR(3) NULL,    emp_pais VARCHAR(80) NULL,        -- Banco empresa (opcional)    emp_chave_pix VARCHAR(120) NULL,    emp_banco VARCHAR(120) NULL,    emp_cod_banco VARCHAR(10) NULL,    emp_agencia VARCHAR(20) NULL,    emp_conta VARCHAR(30) NULL,    emp_tipo_conta ENUM('corrente','poupanca') NULL,    emp_operacao VARCHAR(10) NULL,    emp_favorecido VARCHAR(255) NULL,        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,        FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE,    INDEX idx_user_id (user_id),    INDEX idx_cpf (cpf),    INDEX idx_cnpj (emp_cnpj));-- Script para adicionar novos campos ao banco de dados Controll IT Help Desk-- Execute este script no phpMyAdmin após executar o setup_simple.sqlUSE `helpdesk_titanium`;-- Adicionar campos na tabela users para clientesALTER TABLE `users` ADD COLUMN `client_name` VARCHAR(100) DEFAULT NULL COMMENT 'Nome do cliente (Sonda, Simpress, etc)' AFTER `name`,ADD COLUMN `registration_number` VARCHAR(50) DEFAULT NULL COMMENT 'Matrícula do usuário' AFTER `client_name`;-- Adicionar campos na tabela ticketsALTER TABLE `tickets`ADD COLUMN `requester_name` VARCHAR(100) DEFAULT NULL COMMENT 'Nome de quem está abrindo o chamado' AFTER `user_id`,ADD COLUMN `requester_registration` VARCHAR(50) DEFAULT NULL COMMENT 'Matrícula de quem está abrindo o chamado' AFTER `requester_name`,ADD COLUMN `unit_sigla` VARCHAR(50) DEFAULT NULL COMMENT 'Unidade/Sigla da loja/agência' AFTER `requester_registration`,ADD COLUMN `zipcode` VARCHAR(10) DEFAULT NULL COMMENT 'CEP' AFTER `unit_sigla`,ADD COLUMN `address` VARCHAR(255) DEFAULT NULL COMMENT 'Endereço completo' AFTER `zipcode`,ADD COLUMN `city` VARCHAR(100) DEFAULT NULL COMMENT 'Cidade' AFTER `address`,ADD COLUMN `state` VARCHAR(2) DEFAULT NULL COMMENT 'Estado (UF)' AFTER `city`,ADD COLUMN `technician_value` DECIMAL(10,2) DEFAULT NULL COMMENT 'Valor do técnico' AFTER `resolved_at`,ADD COLUMN `closing_value` DECIMAL(10,2) DEFAULT NULL COMMENT 'Valor final do chamado' AFTER `technician_value`;-- Criar índice para buscas por CEPCREATE INDEX `idx_zipcode` ON `tickets` (`zipcode`);-- Criar índice para buscas por unidadeCREATE INDEX `idx_unit_sigla` ON `tickets` (`unit_sigla`);-- Verificar se funcionouSELECT 'Campos adicionados com sucesso!' as status;DESCRIBE `users`;DESCRIBE `tickets`;