l Support', 'high', 'open', 9, 5, 1, NOW()),('Erro na fatura', 'Fatura incorreta no sistema', 'Billing', 'medium', 'in_progress', 10, 7, 2, NOW()),('Solicitação de nova funcionalidade', 'Gostaria de uma nova funcionalidade X', 'General Inquiry', 'low', 'open', 11, NULL, NULL, NOW()),('Bug no sistema', 'Sistema travando ao salvar', 'Bug Report', 'critical', 'open', 12, 6, 1, NOW()),('Problema de rede', 'Internet lenta na empresa', 'Technical Support', 'medium', 'in_progress', 9, 5, 1, NOW()),('Cobrança duplicada', 'Cobrança apareceu duas vezes', 'Billing', 'high', 'open', 10, 7, 2, NOW());-- Adicionar algumas tags aos ticketsINSERT INTO ticket_tags (ticket_id, tag_id) VALUES(1, 1), (1, 2), -- urgent, bug(2, 1), -- urgent(3, 3), -- feature(4, 1), (4, 2), -- urgent, bug(5, 1), -- urgent(6, 1); -- urgent-- Adicionar alguns comentários de exemploINSERT INTO ticket_comments (ticket_id, comment, created_by, is_internal, created_at) VALUES(1, 'Vou verificar o problema de login', 5, FALSE, NOW()),(2, 'Verificando a fatura no sistema', 7, FALSE, NOW()),(3, 'Analisando a solicitação', 6, TRUE, NOW()),(4, 'Bug crítico identificado, priorizando', 6, FALSE, NOW()),(5, 'Verificando a conexão de rede', 5, FALSE, NOW()),(6, 'Investigando a cobrança duplicada', 7, FALSE, NOW());-- Adicionar algumas notificações de exemploINSERT INTO notifications (user_id, type, title, message, ticket_id, priority, created_at) VALUES(5, 'ticket_assigned', 'Ticket Atribuído', 'Ticket #1 foi atribuído a você', 1, 'high', NOW()),(7, 'ticket_assigned', 'Ticket Atribuído', 'Ticket #2 foi atribuído a você', 2, 'normal', NOW()),(6, 'ticket_assigned', 'Ticket Atribuído', 'Ticket #4 foi atribuído a você', 4, 'critical', NOW()),(5, 'ticket_assigned', 'Ticket Atribuído', 'Ticket #5 foi atribuído a você', 5, 'normal', NOW()),(7, 'ticket_assigned', 'Ticket Atribuído', 'Ticket #6 foi atribuído a você', 6, 'high', NOW());