<?php// Debug das tabelas do bancoecho "<h1>üîç Debug das Tabelas do Banco</h1>";try {    require_once 'api/config.php';        $db = new Database();    $conn = $db->getConnection();        echo "<h2>1. Verificando tabelas existentes:</h2>";        // Listar todas as tabelas    $stmt = $conn->query("SHOW TABLES");    $tables = $stmt->fetchAll(PDO::FETCH_COLUMN);        echo "<p>Tabelas encontradas:</p>";    echo "<ul>";    foreach ($tables as $table) {        echo "<li>$table</li>";    }    echo "</ul>";        // Verificar se as tabelas necess√°rias existem    $requiredTables = ['tickets', 'users', 'ticket_categories', 'ticket_statuses'];    echo "<h2>2. Verificando tabelas necess√°rias:</h2>";        foreach ($requiredTables as $table) {        if (in_array($table, $tables)) {            echo "<p style='color: green;'>‚úÖ Tabela $table existe</p>";                        // Contar registros            $stmt = $conn->query("SELECT COUNT(*) as count FROM $table");            $count = $stmt->fetch()['count'];            echo "<p>Registros: $count</p>";        } else {            echo "<p style='color: red;'>‚ùå Tabela $table n√£o existe</p>";        }    }        // Verificar estrutura da tabela tickets    echo "<h2>3. Estrutura da tabela tickets:</h2>";    if (in_array('tickets', $tables)) {        $stmt = $conn->query("DESCRIBE tickets");        $columns = $stmt->fetchAll();                echo "<table border='1' style='border-collapse: collapse;'>";        echo "<tr><th>Campo</th><th>Tipo</th><th>Null</th><th>Key</th><th>Default</th></tr>";        foreach ($columns as $column) {            echo "<tr>";            echo "<td>" . $column['Field'] . "</td>";            echo "<td>" . $column['Type'] . "</td>";            echo "<td>" . $column['Null'] . "</td>";            echo "<td>" . $column['Key'] . "</td>";            echo "<td>" . $column['Default'] . "</td>";            echo "</tr>";        }        echo "</table>";    }        // Testar query espec√≠fica    echo "<h2>4. Teste da query espec√≠fica:</h2>";    try {        $query = "            SELECT                 t.id, t.title, t.description, t.priority, t.created_at, t.updated_at,                u.name as user_name, u.email as user_email,                tc.name as category,                ts.name as status, ts.slug as status_slug            FROM tickets t            LEFT JOIN users u ON t.user_id = u.id            LEFT JOIN ticket_categories tc ON t.category_id = tc.id            LEFT JOIN ticket_statuses ts ON t.status_id = ts.id            ORDER BY t.created_at DESC            LIMIT 5        ";                $stmt = $conn->prepare($query);        $stmt->execute();        $tickets = $stmt->fetchAll();                echo "<p style='color: green;'>‚úÖ Query executada com sucesso</p>";        echo "<p>Registros encontrados: " . count($tickets) . "</p>";                if (count($tickets) > 0) {            echo "<h3>Primeiro ticket:</h3>";            echo "<pre>" . print_r($tickets[0], true) . "</pre>";        }            } catch (Exception $e) {        echo "<p style='color: red;'>‚ùå Erro na query: " . $e->getMessage() . "</p>";    }    } catch (Exception $e) {    echo "<p style='color: red;'>‚ùå Erro geral: " . $e->getMessage() . "</p>";}echo "<hr>";echo "<h2>üìã Resultado:</h2>";echo "<p>Se houver ‚ùå, me informe qual √© o erro espec√≠fico.</p>";?>