<?php// Script de diagn√≥stico para XAMPPerror_reporting(E_ALL);ini_set('display_errors', 1);echo "<h1>üîß Diagn√≥stico Controll IT Help Desk</h1>";// 1. Verificar PHPecho "<h2>1. Informa√ß√µes do PHP</h2>";echo "PHP Version: " . phpversion() . "<br>";echo "Server: " . $_SERVER['SERVER_SOFTWARE'] . "<br>";// 2. Verificar extens√µesecho "<h2>2. Extens√µes PHP</h2>";$required_extensions = ['pdo', 'pdo_mysql', 'json', 'openssl'];foreach ($required_extensions as $ext) {    $status = extension_loaded($ext) ? '‚úÖ' : '‚ùå';    echo "$status $ext<br>";}// 3. Testar conex√£o com bancoecho "<h2>3. Teste de Conex√£o com Banco</h2>";try {    $pdo = new PDO('mysql:host=localhost;dbname=helpdesk_titanium', 'root', '');    echo "‚úÖ Conex√£o com banco: OK<br>";        // Verificar se as tabelas existem    $tables = $pdo->query("SHOW TABLES")->fetchAll(PDO::FETCH_COLUMN);    echo "Tabelas encontradas: " . count($tables) . "<br>";    foreach ($tables as $table) {        echo "‚Ä¢ $table<br>";    }    } catch (PDOException $e) {    echo "‚ùå Erro na conex√£o: " . $e->getMessage() . "<br>";    echo "<strong>Solu√ß√£o:</strong> Configure o banco de dados<br>";}// 4. Verificar arquivos da APIecho "<h2>4. Arquivos da API</h2>";$api_files = [    'api/config.php',    'api/auth.php',    'api/tickets.php',    'api/users.php'];foreach ($api_files as $file) {    $status = file_exists($file) ? '‚úÖ' : '‚ùå';    echo "$status $file<br>";}// 5. Testar configura√ß√£oecho "<h2>5. Teste de Configura√ß√£o</h2>";if (file_exists('api/config.php')) {    try {        include 'api/config.php';        echo "‚úÖ config.php carregado<br>";        echo "DB_HOST: " . (defined('DB_HOST') ? DB_HOST : 'N√ÉO DEFINIDO') . "<br>";        echo "DB_NAME: " . (defined('DB_NAME') ? DB_NAME : 'N√ÉO DEFINIDO') . "<br>";    } catch (Exception $e) {        echo "‚ùå Erro ao carregar config.php: " . $e->getMessage() . "<br>";    }}// 6. Instru√ß√µesecho "<h2>6. Pr√≥ximos Passos</h2>";echo "<ol>";echo "<li>Se o banco n√£o existe, execute: <code>setup_database.sql</code></li>";echo "<li>Se houver erros de PHP, verifique os logs do Apache</li>";echo "<li>Se tudo estiver OK, acesse: <code>index.html</code></li>";echo "</ol>";echo "<hr>";echo "<p><strong>Status:</strong> " . (count($tables) > 0 ? "‚úÖ Sistema funcionando!" : "‚ùå Configure o banco de dados") . "</p>";?>