<?php// Script para verificar e corrigir configura√ß√£o do phpMyAdminerror_reporting(E_ALL);ini_set('display_errors', 1);echo "<h1>üîß Verifica√ß√£o do phpMyAdmin</h1>";echo "<h2>1. Verificar Configura√ß√£o do PHP:</h2>";echo "PHP Version: " . phpversion() . "<br>";echo "Upload Max Filesize: " . ini_get('upload_max_filesize') . "<br>";echo "Post Max Size: " . ini_get('post_max_size') . "<br>";echo "Max Execution Time: " . ini_get('max_execution_time') . "<br>";echo "Memory Limit: " . ini_get('memory_limit') . "<br>";echo "<h2>2. Verificar Extens√µes:</h2>";$required_extensions = ['mysqli', 'pdo_mysql', 'json', 'openssl'];foreach ($required_extensions as $ext) {    $status = extension_loaded($ext) ? '‚úÖ' : '‚ùå';    echo "$status $ext<br>";}echo "<h2>3. Testar Conex√£o com MySQL:</h2>";try {    $pdo = new PDO('mysql:host=localhost', 'root', '');    echo "‚úÖ Conex√£o com MySQL: OK<br>";        // Verificar se o banco existe    $stmt = $pdo->query("SHOW DATABASES LIKE 'helpdesk_titanium'");    $db = $stmt->fetch();        if ($db) {        echo "‚úÖ Banco 'helpdesk_titanium' existe<br>";                // Verificar tabelas        $pdo->exec("USE helpdesk_titanium");        $stmt = $pdo->query("SHOW TABLES");        $tables = $stmt->fetchAll(PDO::FETCH_COLUMN);                echo "Tabelas encontradas: " . count($tables) . "<br>";        foreach ($tables as $table) {            echo "‚Ä¢ $table<br>";        }            } else {        echo "‚ùå Banco 'helpdesk_titanium' n√£o existe<br>";        echo "<strong>Solu√ß√£o:</strong> Vamos criar o banco...<br>";                // Criar banco        $pdo->exec("CREATE DATABASE IF NOT EXISTS helpdesk_titanium CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci");        echo "‚úÖ Banco criado!<br>";                // Usar o banco        $pdo->exec("USE helpdesk_titanium");                // Criar tabela de usu√°rios        $pdo->exec("            CREATE TABLE IF NOT EXISTS users (                id int(11) NOT NULL AUTO_INCREMENT,                username varchar(50) NOT NULL UNIQUE,                email varchar(100) NOT NULL UNIQUE,                password varchar(255) NOT NULL,                name varchar(100) NOT NULL,                type enum('user','support','admin') NOT NULL DEFAULT 'user',                department varchar(50) DEFAULT NULL,                phone varchar(20) DEFAULT NULL,                created_at timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,                updated_at timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,                is_active tinyint(1) NOT NULL DEFAULT 1,                PRIMARY KEY (id)            ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci        ");        echo "‚úÖ Tabela 'users' criada!<br>";                // Inserir usu√°rio admin        $password = password_hash('Controll IT@10', PASSWORD_BCRYPT, ['cost' => 12]);        $stmt = $pdo->prepare("INSERT INTO users (username, email, password, name, type, department, phone) VALUES (?, ?, ?, ?, ?, ?, ?)");        $stmt->execute(['admin', 'admin@controllit.com.br', $password, 'Administrador do Sistema', 'admin', 'TI', '11999999999']);        echo "‚úÖ Usu√°rio admin criado!<br>";    }    } catch (PDOException $e) {    echo "‚ùå Erro na conex√£o: " . $e->getMessage() . "<br>";}echo "<h2>4. Instru√ß√µes para Corrigir phpMyAdmin:</h2>";echo "<ol>";echo "<li><strong>Reiniciar XAMPP:</strong> Pare e inicie novamente o Apache e MySQL</li>";echo "<li><strong>Limpar Cache:</strong> Pressione Ctrl+F5 no navegador</li>";echo "<li><strong>Acessar phpMyAdmin:</strong> <a href='http://localhost/phpmyadmin' target='_blank'>http://localhost/phpmyadmin</a></li>";echo "<li><strong>Se ainda houver erro:</strong> Verificar arquivo config.inc.php do phpMyAdmin</li>";echo "</ol>";echo "<h2>5. Testar Sistema:</h2>";echo "<p><strong>Ap√≥s corrigir:</strong> <a href='http://localhost/sistema-web-Controll IT/'>Testar Sistema</a></p>";?>