<?php// Script para atualizar senhas para Controll IT@10error_reporting(E_ALL);ini_set('display_errors', 1);echo "<h1>🔐 Atualizando Senhas para Controll IT@10</h1>";try {    // Conectar ao banco    $pdo = new PDO('mysql:host=localhost;dbname=helpdesk_titanium', 'root', '');    echo "✅ Conexão com banco: OK<br>";        // Nova senha    $newPassword = 'Controll IT@10';    $newHash = password_hash($newPassword, PASSWORD_BCRYPT, ['cost' => 12]);        echo "<h2>🔄 Atualizando Senhas:</h2>";        // Atualizar todos os usuários    $users = ['admin', 'suporte', 'usuario'];        foreach ($users as $username) {        $stmt = $pdo->prepare("UPDATE users SET password = ? WHERE username = ?");        $result = $stmt->execute([$newHash, $username]);                if ($result) {            echo "✅ Senha atualizada para usuário: $username<br>";        } else {            echo "❌ Erro ao atualizar senha para: $username<br>";        }    }        echo "<h2>🧪 Testando Novas Senhas:</h2>";        // Testar a senha para cada usuário    foreach ($users as $username) {        $stmt = $pdo->prepare("SELECT password FROM users WHERE username = ?");        $stmt->execute([$username]);        $user = $stmt->fetch();                if ($user) {            $isValid = password_verify($newPassword, $user['password']);            $status = $isValid ? "✅ VÁLIDA" : "❌ INVÁLIDA";            echo "Usuário $username: $status<br>";        }    }        echo "<hr>";    echo "<h2>📋 Credenciais Atualizadas:</h2>";    echo "<table border='1' style='border-collapse: collapse; margin: 10px 0;'>";    echo "<tr><th>Usuário</th><th>Senha</th><th>Tipo</th></tr>";    echo "<tr><td>admin</td><td>Controll IT@10</td><td>admin</td></tr>";    echo "<tr><td>suporte</td><td>Controll IT@10</td><td>support</td></tr>";    echo "<tr><td>usuario</td><td>Controll IT@10</td><td>user</td></tr>";    echo "</table>";        echo "<h2>🚀 Próximos Passos:</h2>";    echo "<ol>";    echo "<li>Acesse: <a href='http://localhost/sistema-web-Controll IT/'>http://localhost/sistema-web-Controll IT/</a></li>";    echo "<li>Faça login com: <strong>admin</strong> / <strong>Controll IT@10</strong></li>";    echo "<li>O sistema deve funcionar perfeitamente!</li>";    echo "</ol>";    } catch (PDOException $e) {    echo "❌ Erro na conexão: " . $e->getMessage() . "<br>";}?>