<?phpecho "Teste de conexão com o banco de dados no Heroku<br><br>";// Verificar variáveis de ambienteecho "DATABASE_URL: " . (isset($_ENV['DATABASE_URL']) ? 'Configurada' : 'NÃO configurada') . "<br>";echo "JWT_SECRET: " . (isset($_ENV['JWT_SECRET']) ? 'Configurada' : 'NÃO configurada') . "<br>";echo "HEROKU_APP_NAME: " . (isset($_ENV['HEROKU_APP_NAME']) ? $_ENV['HEROKU_APP_NAME'] : 'NÃO configurada') . "<br><br>";// Testar conexão com bancotry {    if (isset($_ENV['DATABASE_URL'])) {        $url = parse_url($_ENV['DATABASE_URL']);        $host = $url['host'];        $dbname = ltrim($url['path'], '/');        $user = $url['user'];        $pass = $url['pass'];        $port = $url['port'] ?? 5432;                $dsn = "pgsql:host=$host;port=$port;dbname=$dbname";        $pdo = new PDO($dsn, $user, $pass);                echo "✅ Conexão com PostgreSQL estabelecida com sucesso!<br>";                // Testar se as tabelas existem        $stmt = $pdo->query("SELECT table_name FROM information_schema.tables WHERE table_schema = 'public'");        $tables = $stmt->fetchAll(PDO::FETCH_COLUMN);                echo "Tabelas encontradas: " . implode(', ', $tables) . "<br><br>";                if (empty($tables)) {            echo "⚠️ Nenhuma tabela encontrada. Execute o setup do banco.<br>";        } else {            echo "✅ Banco de dados configurado corretamente!<br>";        }            } else {        echo "❌ DATABASE_URL não configurada!<br>";    }    } catch (Exception $e) {    echo "❌ Erro na conexão: " . $e->getMessage() . "<br>";}echo "<br><a href='index.php'>Voltar para o sistema</a>";?>