<?php// Teste da API de loginheader('Content-Type: application/json');// Simular dados de login$input = [    'username' => 'douglas@controllit.com.br',    'password' => 'senha123'];echo "🔍 Testando API de login...\n";echo "Dados enviados: " . json_encode($input) . "\n";// Incluir a API de authtry {    require_once 'api/auth.php';        // Simular requisição POST    $_SERVER['REQUEST_METHOD'] = 'POST';        // Simular dados de entrada    $GLOBALS['input_data'] = json_encode($input);        // Capturar output    ob_start();        // Incluir o arquivo de auth    include 'api/auth.php';        $output = ob_get_clean();    echo "Resposta da API: " . $output . "\n";    } catch (Exception $e) {    echo "❌ Erro na API: " . $e->getMessage() . "\n";}?>