<?php// Script para testar diferentes configurações de conexão MySQLecho "🔍 Testando configurações de conexão MySQL...\n\n";$configs = [    ['host' => 'localhost', 'user' => 'root', 'pass' => ''],    ['host' => 'localhost', 'user' => 'root', 'pass' => 'root'],    ['host' => 'localhost', 'user' => 'root', 'pass' => 'password'],    ['host' => '127.0.0.1', 'user' => 'root', 'pass' => ''],    ['host' => '127.0.0.1', 'user' => 'root', 'pass' => 'root'],    ['host' => '127.0.0.1', 'user' => 'root', 'pass' => 'password'],];foreach ($configs as $i => $config) {    echo "Teste " . ($i + 1) . ": {$config['host']} - {$config['user']} - " . (empty($config['pass']) ? 'sem senha' : 'com senha') . "\n";        try {        $dsn = "mysql:host={$config['host']};port=3306;charset=utf8mb4";        $pdo = new PDO($dsn, $config['user'], $config['pass'], [            PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,            PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC        ]);                echo "✅ SUCESSO! Conexão estabelecida\n";        echo "   Host: {$config['host']}\n";        echo "   User: {$config['user']}\n";        echo "   Password: " . (empty($config['pass']) ? 'vazia' : 'definida') . "\n";                // Testar criação de banco        $pdo->exec("CREATE DATABASE IF NOT EXISTS helpdesk_titanium CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci");        echo "✅ Banco de dados criado com sucesso\n";                // Salvar configuração que funcionou        $workingConfig = $config;        break;            } catch (PDOException $e) {        echo "❌ Falhou: " . $e->getMessage() . "\n";    }    echo "\n";}if (isset($workingConfig)) {    echo "\n🎉 Configuração funcionando encontrada!\n";    echo "Vou atualizar o arquivo de configuração...\n";        // Atualizar config-local.php com a configuração que funcionou    $configContent = file_get_contents('api/config-local.php');        if ($workingConfig['host'] !== 'localhost') {        $configContent = str_replace("define('DB_HOST', 'localhost');", "define('DB_HOST', '{$workingConfig['host']}');", $configContent);    }        if (!empty($workingConfig['pass'])) {        $configContent = str_replace("define('DB_PASS', '');", "define('DB_PASS', '{$workingConfig['pass']}');", $configContent);    }        file_put_contents('api/config-local.php', $configContent);    echo "✅ Arquivo de configuração atualizado\n";        echo "\n📝 Configuração funcionando:\n";    echo "Host: {$workingConfig['host']}\n";    echo "User: {$workingConfig['user']}\n";    echo "Password: " . (empty($workingConfig['pass']) ? 'vazia' : 'definida') . "\n";    } else {    echo "\n❌ Nenhuma configuração funcionou\n";    echo "\n🔧 Possíveis soluções:\n";    echo "1. Verifique se o XAMPP Control Panel está rodando\n";    echo "2. Clique em 'Start' no MySQL no XAMPP Control Panel\n";    echo "3. Verifique se não há senha configurada no MySQL\n";    echo "4. Tente reiniciar o XAMPP completamente\n";    echo "5. Verifique se não há outro MySQL rodando na porta 3306\n";}?>