<?php// Teste para verificar se a API de tickets está funcionandoecho "<h1>Teste de Exibição de Tickets</h1>";// Simular chamada para a API$url = 'http://localhost/sistema-web-Controll IT/api/tickets.php?action=list';$context = stream_context_create([    'http' => [        'method' => 'GET',        'header' => 'Content-Type: application/json'    ]]);echo "<h2>Testando API de Tickets:</h2>";echo "<p>URL: $url</p>";try {    $response = file_get_contents($url, false, $context);        if ($response === false) {        echo "<p style='color: red;'>Erro: Não foi possível conectar à API</p>";    } else {        echo "<p style='color: green;'>✅ API respondeu com sucesso!</p>";                $data = json_decode($response, true);                if ($data && isset($data['success']) && $data['success']) {            echo "<p style='color: green;'>✅ Resposta da API válida</p>";            echo "<p>Total de tickets: " . count($data['data']) . "</p>";                        if (count($data['data']) > 0) {                echo "<h3>Primeiros 3 tickets:</h3>";                echo "<table border='1' style='border-collapse: collapse; width: 100%;'>";                echo "<tr><th>ID</th><th>Título</th><th>Status</th><th>Prioridade</th><th>Usuário</th><th>Data</th></tr>";                                for ($i = 0; $i < min(3, count($data['data'])); $i++) {                    $ticket = $data['data'][$i];                    echo "<tr>";                    echo "<td>#" . htmlspecialchars($ticket['id']) . "</td>";                    echo "<td>" . htmlspecialchars($ticket['title']) . "</td>";                    echo "<td>" . htmlspecialchars($ticket['status']) . "</td>";                    echo "<td>" . htmlspecialchars($ticket['priority']) . "</td>";                    echo "<td>" . htmlspecialchars($ticket['user_name'] ?? 'N/A') . "</td>";                    echo "<td>" . htmlspecialchars($ticket['created_at']) . "</td>";                    echo "</tr>";                }                echo "</table>";            } else {                echo "<p style='color: orange;'>⚠️ Nenhum ticket encontrado no banco</p>";            }        } else {            echo "<p style='color: red;'>❌ Resposta da API inválida</p>";            echo "<pre>" . htmlspecialchars($response) . "</pre>";        }    }} catch (Exception $e) {    echo "<p style='color: red;'>Erro: " . $e->getMessage() . "</p>";}echo "<hr>";echo "<h2>Teste de JavaScript:</h2>";echo "<p>Abra o console do navegador (F12) e execute:</p>";echo "<pre>";echo "fetch('http://localhost/sistema-web-Controll IT/api/tickets.php?action=list')\n";echo "  .then(response => response.json())\n";echo "  .then(data => console.log('Tickets:', data));";echo "</pre>";?>